import { useEffect, useState } from 'react';
import { usePeer } from './lib/PeerProvider';

export function Call({remoteUser}) {
    const {peer, peerId} = usePeer();
    const [callId,setCallId] = useState('');
    let callInitiated = false;
    useEffect(() => {
        if(remoteUser && !callInitiated) {
            callInitiated = true;
            fetch("http://localhost:3000/getUserId/" + remoteUser)
                .then((res) => res.json())
                .then((data) => {
                    // initiateCall(data.userId)
                });
        }
    },[]);

    function initiateCall() {
        const remoteUserId = callId;
        console.log(remoteUserId);
        // let getUserMedia = navigator.getUserMedia || navigator.webkitGetUserMedia || navigator.mozGetUserMedia;
        // getUserMedia({video: true, audio: true}, function(stream) {
        //     var call = peer.call(remoteUserId[0], stream);
        //     console.log(remoteUserId[0]);
        //     call.on('stream', function(remoteStream) {
        //         // Show stream in some video/canvas element.
        //         console.log('received remotestream');
        //     });
        // }, function(err) {
        // console.log('Failed to get local stream' ,err);
        // });
        var conn = peer.connect(remoteUserId);

        // // on open will be launch when you successfully connect to PeerServer
        conn.on('open', function(){
        // here you have conn.id
        conn.send('hi!');
        });
    }

    return <div>
            Calling {remoteUser}
            <br></br>
            <input id="dsds" type="text" value={callId} onChange={e => setCallId(e.target.value)}></input>
            <button onClick={() => initiateCall()}>Click me</button>
        </div>
}
