import { useEffect, useState } from 'react';
import { usePeer } from './lib/PeerProvider';

export function Call({remoteUser}) {
    const {peer, peerId} = usePeer();
    const [otherUser,setOtherUser] = useState(null);

    useEffect(() => {
        console.log(otherUser);
        if(remoteUser && !otherUser) {
            fetch("http://localhost:3000/getUserId/" + remoteUser)
                .then((res) => res.json())
                .then((data) => {
                    setOtherUser(data.userId);
                    initiateCall(data.userId)
                });
        }
    },[]);

    function initiateCall(remoteUserId) {
        if(peer){
            console.log("initiating call");
            const conn = peer.connect(remoteUserId);
            if(conn) {
                console.log("called");
                conn.on('open', () => {
                    conn.on('data', function(data) {
                        console.log('Received', data);
                    });

                    // Send messages
                    conn.send('Hello!');
                });
         }
        }
    }

    return <div>
            Calling {remoteUser}
            <div>Got remoteUserId {otherUser}</div>
        </div>
}
